import React, { useEffect, useState } from 'react';
import { Table, Button } from 'semantic-ui-react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function User(){
  const [apiData, setApiData] = useState([]);
  useEffect(() => {
      axios.get(`https://backend-for-cherie-homes.herokuapp.com/users`)
          .then((getData) => {
              setApiData(getData.data);
          })
  }, [])
    const setData = (id, Name, Contact, Email) => {
      localStorage.setItem('id', id)
    localStorage.setItem('name', Name)
    localStorage.setItem('contact', Contact)
    localStorage.setItem('email', Email)
}

const getData = () => {
  axios.get(`https://backend-for-cherie-homes.herokuapp.com/users`)
      .then((data) => {
          setApiData(data);
      })
}
const onDelete = (id) => {
  axios.delete(`https://backend-for-cherie-homes.herokuapp.com/users/${id}`)
  .then(() => {
      getData();
  })
}

  return(
    <>
       <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>id</Table.HeaderCell>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Contact</Table.HeaderCell>
                        <Table.HeaderCell>Email</Table.HeaderCell>
                        <Table.HeaderCell>Update</Table.HeaderCell>
                        <Table.HeaderCell>Delete</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {apiData.map((data) => {
                        return (
                            <Table.Row>
                                <Table.Cell>{data.id}</Table.Cell>
                                <Table.Cell>{data.Name}</Table.Cell>
                                <Table.Cell>{data.Contact}</Table.Cell>
                                <Table.Cell>{data.Email}</Table.Cell>
                                <Table.Cell>
                                    <Link to='/update'>
                                        <Button
                                            color="green"
                                            onClick={() => setData(data.id, data.Name, data.Contact, data.Email)}>
                                            Update
                                        </Button>
                                    </Link>
                                </Table.Cell>
                                <Table.Cell>
                                    <Button color="red" onClick={() => onDelete(data.id)}>Delete</Button>
                                </Table.Cell>
                            </Table.Row>
                        )
                    })}

                </Table.Body>
            </Table>
    </>
  )
}

export default User;
