import React from 'react'
import { useState, useEffect } from 'react'

function Vendor() {






 // const addHouse = () => {
  //   fetch('http://localhost:9292/create', {
  //     method: 'POST',
  //     headers: { 'Content-Type': 'application/json' },
  //     body: JSON.stringify({
  //       HouseType: HouseType,
  //       Location: Location,
  //       EstimatedCost: EstimatedCost,
  //       NumberOfRooms: NumberOfRooms,
  //       FloorNumber: FloorNumber,
  //     })
  //   })
  //   .then(() => r.json())
  // }







  const [vendors, setVendors] = useState([])

  useEffect(() => {
    fetch('http://localhost:9292/vendors')
      .then((r) => r.json())
      .then((games) => setVendors(vendors))
  }, [])

  return (
    <>
      {vendors.map((val, key) => (
        <div>
          <h3>vendor:{val.name}</h3>
          <h3>vendor:{val.phonenumber}</h3>
        </div>
      ))}
    </>
  )
}

function ReviewItem({ review, onDeleteReview }) {
    function handleDeleteClick() {
      fetch(`http://localhost:9292/reviews/${review.id}`, {
        method: "DELETE",
      })
        .then((r) => r.json())
        .then((deletedReview) => onDeleteReview(deletedReview));
    }

    return (
      <div>
        <p>Score: {review.score}</p>
        <p>{review.comment}</p>
        <button onClick={handleDeleteClick}>Delete Review</button>
      </div>
    );
  }

export default Vendor
