import React from 'react'
import '../App.css'
import { useState } from 'react'

function Homes() {
  // USE STATE
  const [HouseType, setHouseType] = useState('')
  const [Location, setLocation] = useState('')
  const [EstimatedCost, setEstimatedCost] = useState('')
  const [NumberOfRooms, setNumberOfRooms] = useState(0)
  const [FloorNumber, setFloorNumber] = useState(0)

    USE-EFFECT
const [houses, setHouses] = useState([])

  //   useEffect(() => {
  //     fetch('http://localhost:9292/houses')
  //       .then((r) => r.json())
  //       .then(setHouses)
  //   }, [])

      // GET METHOD
  const getHouse = () => {
      fetch('http://localhost:9292/houses')
      .then((r) => r.json())
      .then(setHouses(r.json))
  }

      //POST METHOD
  const addHome = () => {
    fetch('http://localhost:9292/create', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        HouseType: HouseType,
        Location: Location,
        EstimatedCost: EstimatedCost,
        NumberOfRooms: NumberOfRooms,
        FloorNumber: FloorNumber,
      }),
    }).then((r) => r.json())
  }

  return (
    <>
      <div className="homes">
        <div className="homes-info"></div>
        <label>House Type:</label>
        <input
          class="w3-input w3-border w3-round-large"
          type="text"
          onChange={(event) => {
            setHouseType(event.target.value)
          }}
        />
        <label>Location:</label>
        <input
          class="w3-input w3-border w3-round-large"
          type="text"
          onChange={(event) => {
            setLocation(event.target.value)
          }}
        />
        <label>Estimated Cost:</label>
        <input
          class="w3-input w3-border w3-round-large"
          type="text"
          onChange={(event) => {
            setEstimatedCost(event.target.value)
          }}
        />
        <label>Number Of Rooms:</label>
        <input
          class="w3-input w3-border w3-round-large"
          type="number"
          onChange={(event) => {
            setNumberOfRooms(event.target.value)
          }}
        />
        <label>Floor Number:</label>
        <input
          class="w3-input w3-border w3-round-large"
          type="number"
          onChange={(event) => {
            setFloorNumber(event.target.value)
          }}
        />
      </div>
      <div className="butto">
        <button onClick={addHome}>Add House</button>
        <br/>
        <button onClick={addHome}>Show House</button>
        <br/>
        <button onClick={addHome}>DeleTE House</button>
      </div>


      {/* DISPLAY */}
      <div className="houses">
        <button onClick={geHouse} class="btn btn-outline-primary">
          Show House
        </button>
        {getHouse.map((val, key) => {
          return (
            <div>
              <h3>HouseType:{val.HouseType}</h3>
            </div>
          )
        })}
      </div>
    </>
  )
}
export default Homes
