import React, { useEffect, useState } from 'react';
import { Table, Button } from 'semantic-ui-react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function User(){
  const [apiData, setApiData] = useState([]);
  useEffect(() => {
      axios.get(`https://backend-for-cherie-homes.herokuapp.com/users`)
          .then((resp) => {
              console.log("data:", resp.data);
              setApiData(resp.data);
          })
  }, [])
    const setData = (id, Name, Contact, Email) => {
      localStorage.setItem('id', id)
    localStorage.setItem('name', Name)
    localStorage.setItem('contact', Contact)
    localStorage.setItem('email', Email)
}

const getData = () => {
  axios.get(`https://backend-for-cherie-homes.herokuapp.com/users`)
      .then((resp) => {
          setApiData(resp.data);
      })
}
const onDelete = (id) => {
  axios.delete(`https://backend-for-cherie-homes.herokuapp.com/users/${id}`)
  .then((resp) => {
      console.log(`User ID: ${id} Deleted`);
      console.log
      getData();
  })
}

  return(
    <>
       <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>id</Table.HeaderCell>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Contact</Table.HeaderCell>
                        <Table.HeaderCell>Email</Table.HeaderCell>
                        <Table.HeaderCell>Update</Table.HeaderCell>
                        <Table.HeaderCell>Delete</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {apiData.length === 0 ? "Loading..." : apiData.map((value) => (
                            <Table.Row key={value.id}>
                                <Table.Cell>{value.id}</Table.Cell>
                                <Table.Cell>{value.name}</Table.Cell>
                                <Table.Cell>{value.contact}</Table.Cell>
                                <Table.Cell>{value.email}</Table.Cell>
                                <Table.Cell>
                                    <Link to='/update'>
                                        <Button
                                            color="green"
                                            onClick={() => setData(value.id, value.name, value.contact, value.email)}>
                                            Update
                                        </Button>
                                    </Link>
                                </Table.Cell>
                                <Table.Cell>
                                    <Button color="red" onClick={() => onDelete(value.id)}>Delete</Button>
                                </Table.Cell>
                            </Table.Row>

                    ))}

                </Table.Body>
            </Table>
    </>
  )
}

export default User;
