import React from 'react'
import { useState, useEffect } from 'react'

function Homes() {
  // USE STATE
  const [HouseType, setHouseType] = useState('')
  const [Location, setLocation] = useState('')
  const [EstimatedCost, setEstimatedCost] = useState('')
  const [NumberOfRooms, setNumberOfRooms] = useState(0)
  const [FloorNumber, setFloorNumber] = useState(0)

  //   USE-EFFECT
  const [houses, setHouses] = useState([])

//   useEffect(() => {
//     fetch('http://localhost:9292/houses')
//       .then((r) => r.json())
//       .then(setHouses)
//   }, [])

// const getHouse = () => {
//     fetch('http://localhost:9292/houses')
//     .then(() => r.json())
//     .then(setHouses)
// }


  //   POST METHOD
  const addHome = () => {
    fetch('http://localhost:9292/create', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        HouseType: HouseType,
        Location: Location,
        EstimatedCost: EstimatedCost,
        NumberOfRooms: NumberOfRooms,
        FloorNumber: FloorNumber,
      }),
    }).then((r) => r.json())
  }

  return (
    <>
      <label>House Type:</label>
      <input
        class="w3-input w3-border w3-round-large"
        type="text"
        onChange={(event) => {
          setHouseType(event.target.value)
        }}
      />
      <label>Location:</label>
      <input
        class="w3-input w3-border w3-round-large"
        type="text"
        onChange={(event) => {
          setLocation(event.target.value)
        }}
      />
      <label>Estimated Cost:</label>
      <input
        class="w3-input w3-border w3-round-large"
        type="text"
        onChange={(event) => {
          setEstimatedCost(event.target.value)
        }}
      />
      <label>Number Of Rooms:</label>
      <input
        class="w3-input w3-border w3-round-large"
        type="number"
        onChange={(event) => {
          setNumberOfRooms(event.target.value)
        }}
      />
      <label>Floor Number:</label>
      <input
        class="w3-input w3-border w3-round-large"
        type="number"
        onChange={(event) => {
          setFloorNumber(event.target.value)
        }}
      />
      <button class="w3-input w3-border w3-round-large" onClick={addHome}>
        Add House
      </button>
      {/* DISPLAY */}
      <div className="buttons">
        {/* <Button>ADD HOUSE</Button>
        <Button>EDIT HOUSE</Button>
        <Button>DELETE HOUSE</Button> */}
      </div>
      <div className="houses">
        <button onClick={getHouse} class="btn btn-outline-primary">
          Show House
        </button>
        {getHouse.map((val, key) => {
          return (
            <div>
              <h3>HouseType:{val.HouseType}</h3>
            </div>
          )
        })}
      </div>
    </>
  )
}
export default Homes
