import React, { useEffect, useState } from 'react';
import { Table, Button } from 'semantic-ui-react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function User(){
  const [apiData, setApiData] = useState([]);
  const [showAddUserForm, setShowAddUserForm] =useState(false);

  let values =  {
    name:"",
    contact: "",
    email: ""
  }

  const [userData, setUserData] = useState(values);

  useEffect(() => {
      axios.get(`https://backend-for-cherie-homes.herokuapp.com/users`)
          .then((resp) => {
              console.log("data:", resp.data);
              setApiData(resp.data);
          })
  }, [])
  const setData = (id, Name, Contact, Email) => {
      localStorage.setItem('id', id)
      localStorage.setItem('name', Name)
      localStorage.setItem('contact', Contact)
      localStorage.setItem('email', Email)
  }

  const getData = () => {
    axios.get(`https://backend-for-cherie-homes.herokuapp.com/users`)
      .then((resp) => {
          setApiData(resp.data);
      }
    )
  }

  const onDelete = (id) => {
    axios.delete(`https://backend-for-cherie-homes.herokuapp.com/users/${id}`)
    .then((res) => {
        console.log(`User ID: ${id} Deleted`);
        console.log(res.data);
        getData();
    })
  }

  const handleChange = (values) => {
    setUserData(values);
  }

  const addUser = () => {
    setShowAddUserForm(true);
  }

  const saveUser =(values) => {
    axios.post(`https://backend-for-cherie-homes.herokuapp.com/create_user?name=${values.name}&contact=${values.name}&&email=mannaseh@gmail.com`).then((res) => { console.log("new user: ", res.data)})
  }

  return(

    <>

        <div className="d-grid gap-2">
          <button className="btn btn-primary" type="button" onClick={() => addUser()}>Add User</button>
        </div>

       <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>id</Table.HeaderCell>
                        <Table.HeaderCell>Name</Table.HeaderCell>
                        <Table.HeaderCell>Contact</Table.HeaderCell>
                        <Table.HeaderCell>Email</Table.HeaderCell>
                        <Table.HeaderCell>Update</Table.HeaderCell>
                        <Table.HeaderCell>Delete</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>
                    {apiData.length === 0 ? "Loading..." : apiData.map((value) => (
                            <Table.Row key={value.id}>
                                <Table.Cell>{value.id}</Table.Cell>
                                <Table.Cell>{value.name}</Table.Cell>
                                <Table.Cell>{value.contact}</Table.Cell>
                                <Table.Cell>{value.email}</Table.Cell>
                                <Table.Cell>
                                    <Link to='/update'>
                                        <Button
                                            color="green"
                                            onClick={() => setData(value.id, value.name, value.contact, value.email)}>
                                            Update
                                        </Button>
                                    </Link>
                                </Table.Cell>
                                <Table.Cell>
                                    <Button color="red" onClick={() => onDelete(value.id)}>Delete</Button>
                                </Table.Cell>
                            </Table.Row>

                    ))}

                </Table.Body>
            </Table>

            <br />

            {/* form for adding user */}
            { showAddUserForm === true ? (
              <div className="d-flex justify-content-center">
                <form>
                  <fieldset>
                    <div class="form-group">
                        <label for="name" class="form-label mt-4">Name</label>
                        <input type="text" class="form-control" id="name" value={values.name} placeholder="Enter Name" />
                    </div>
                    <div class="form-group">
                        <label for="contact" class="form-label mt-4">Contact</label>
                        <input type="number" class="form-control" id="contact" value={values.contact} placeholder="Enter Contact" />
                    </div>
                    <div class="form-group">
                        <label for="email" class="form-label mt-4">Email</label>
                        <input type="email" class="form-control" id="email" value={values.email} placeholder="Enter Email" />
                    </div>
                  </fieldset>
                  <button type="submit" class="btn btn-primary" onClick={() => saveUser(values)}>Submit</button>
                </form>
              </div>
            ) : ( console.log("form_hidden"))}

    </>


  )
}

export default User;
