      <button onClick={getHouse} class="btn btn-outline-primary">Show House</button>
      {getHouse.map((val,key) => {
        return<div>
          <h3>HouseType:{val.HouseType}</h3>
        </div>
      })}
      </div>
    </div>
  )
}
